var flag = "";

var adm=function(a){
	var b=/^1|0\d{11}$|^\d{6}[1-9]\d{2}0$/;
	var c=/^0{8}[1-9]?\w[1-9]\d*$/;

	var e=b.test(a);
	var f=c.test(a);

	if(e == true && bc(a)  && alxd(a) != "99") {
		return true;
	}else{
		return false;
	}
};

var bc=function(a){
	var b;
	var d=new Date();
	var e=d.getFullYear();
	var f=e.toString();
	var g=f.substring(2);
	if(a.length==12){
		b=a.substring(5,7);
	}else{
		b=a.substring(4,6);
	}
	//console.log(b + " " + g);
	if(b<=00||b>g){
		return false;
	}
	return true;
};

var ahm=function(a){
	var b=/^\d{8}$/;
	var c=/^0{8}$/;
	var d=/^0{11}-?[1-9]*\w\d*$/;
	var e=b.test(a);
	var f=c.test(a);
	if(e==true&&f==false){
		return true;
	}else{
		return false;
	}
};

var acq=function(a){
	var b=/^\d{8}$/;
	var c=/^0{8}$/;
	var d=/^0{11}-?[1-9]*\w\d*$/;
	var e=b.test(a);
	if(e==true){
		var g=new Date();
		var h=g.getFullYear();
		var i=g.getMonth()+1;
		var j=g.getDate();
		var k=a.substring(0,4);
		var l=parseInt(a.substring(4,6), 10);
		var m=parseInt(a.substring(6), 10);
		var n=ca(0);
		var t=ca(1);
		if((h!=k&&h-1!=k)||l == 0 || l>12|| m == 0 || m>31 || a>n||!cb(k,l,m)||(h==k && i== l && j==m)){
			return false;
		}		
		if(h-1==k&&a<=t){
			return false;
		}
		return true;
	}else{
		return false;
	}
};


function ca(i){
	var a = new Date();
	var b = 0;
	var c = 0;
	var d = 0;
	var e = "";
	b= a.getFullYear()-i;
	c= a.getMonth()+1;
	d = a.getDate();
	e += b;
	if (c >= 10 ){
		e += c;
	}else{
		e += "0" + c;
	}
	if (d >= 10 ){
		e += d ;
	}else{
		e += "0" + d ;
	}
	return e;
}
