function cb(a,b,c){
	if(c>31){
		return false;
	}else if(c<1){
		return false;
	}else{
		if(b==2){
			if(c>29){//2月不会超过29
				return false;
			}
			if (((a % 4)==0) && ((a % 100)!=0) || ((a % 400)==0)) {//闰年1--29
				return true;
			}else{//平年1--28
				if(c>28){
					return false;
				}
			}
		}else if((b==4||b==6||b==9||b==11)&&c>30){
			return false;
		}
	}
	return true;
}
var alxd=function(a){
	var b;
	var c="99";

	if(a.length==12){
		b=a.substring(7,8);
		for(var i=0;i<code.length;i++){
			if(a==code[i]){
				c="10";
				break;
			}
		}
		if (c == "99") {  //增加判断，判断是否为新版电子票
			if (a.charAt(0) == '0' && a.substring(10,12) == '11') {
				c="10";
			}
			if (a.charAt(0) == '0' && (a.substring(10,12) == '06' || a.substring(10,12) == '07')) {  //判断是否为卷式发票  第1位为0且第11-12位为06或07
				c="11";
			} 
		}
		if(c=="99"){ //如果还是99，且第8位是2，则是机动车发票
			if (b==2 && a.charAt(0) != '0') {
				c="03";
			}
		}		 

	}else if(a.length==10){
		b=a.substring(7,8);
		if(b==1||b==5){
			c="01";
		}else if(b==6||b==3){
			c="04";
		}else if(b==7||b==2){
			c="02";
		}
	}
	return c;
};

var aje=function(a,b){
	var c=alxd(a);
	if(c=="01"||c=="02"||c=="03"){
		return ea(b);
	}else{
		return eb(b);
	}
};

var ea=function(a){
	var b=/(^-?\d{1,11}$)|(^-?\d{1,11}\.\d{1,2}$)/;
	var c=b.test(a);
	return c;
};

var eb=function(a){
	var b=/^-?(\d+$)|(\d+\.\d{1,2})$/;
	var c=b.test(a);
	return c;
};

var ajy=function(a){
	var b=/^\d{6}$/;
	var e=b.test(a);
	return e;
};

function avym(yzm){
  var code;
  for(var i=0;i<yzm.length;i++){
    code=yzm.charCodeAt(i);
    if((code>65248)||(code==12288)){
		//console.log(code);
      return false;
    }
  }
  return true;
}

