    if(fpdm==""||fphm==""||kprq==""||kjje==""){ //||yzm==""){    
		return false;
    } else {
		if (yzm == "" || yzm == "请输入验证码") {
			//console.log("yzmm null ");
			return false;
		} else {
			//console.log("yzmm : " + yzm);
			return true;
		}
	}
    //return true;        
}

