	if(fpdm==""||fphm==""||kprq==""||kjje=="" ||yzm=="" || yzm=="请输入验证码"){    
		jAlert('请先输入各项信息！', '提示');
		return false;
	} else {
		return true;
	}
	/*
	var username = getCookie('username');
	if (username == "") { //未登录
		return false;
	} else {
		*/
		return true;
	//}
}

