      $('#uncheckfp').show();
    }
}


function getSwjg(fpdm, ckflag){
  var citys=[{'code':'1100','sfmc':'北京','Ip':'https://zjfpcyweb.bjsat.gov.cn:443','address':'https://zjfpcyweb.bjsat.gov.cn:443'},
